def groupes
    puts "Tapez 'entrée' pour obtenir la prochaine info sur nos journalistes ! :D"
    gets.chomp
    puts "Voici les statistiques relatives au nombre de lettres qui composent les pseudos utilisés par les journalistes :"
    puts
    puts "Tapez 'entrée' pour afficher la liste"
    gets.chomp
    (1..33).each do |x|
        a = x
        puts "Le nombre de journalistes ayant un pseudo à #{x} caractères est égal à : #{liste.count {|x| x.length == a}}"
        puts
end
end

